<?	$param = $_GET["artista"];					/* recollim el par�metre passat per url (i.e http://nomarxiu.php?artista=0) */		$xsl_filename = "archivoXSLT.xsl";	/* nom arxiu xsl */	$xml_filename = "archivoXML.xml";	/* nom arxiu xml */	$doc = new DOMDocument();	$xsl = new XSLTProcessor();	$doc->load($xsl_filename);		$xsl->importStyleSheet($doc);	$xsl->setParameter('', 'param',$param);	/* pasam par�metre al xsl */		$doc->load($xml_filename);		echo $xsl->transformToXML($doc);?>